const Hero: React.FC = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentSlide((prev) => (prev === heroSlides.length - 1 ? 0 : prev + 1));
        }, 5000);
        return () => clearInterval(timer);
    }, []);

    return (
        <section id="home" className="relative h-screen w-full overflow-hidden">
            {heroSlides.map((slide, index) => (
                <div
                    key={index}
                    className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${index === currentSlide ? 'opacity-100' : 'opacity-0'}`}
                    style={{ backgroundImage: `url(${slide.image})`, backgroundSize: 'cover', backgroundPosition: 'center' }}
                >
                    <div className="absolute inset-0 bg-black bg-opacity-60"></div>
                </div>
            ))}
            <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white px-4">
                <div className="max-w-4xl">
                   {heroSlides.map((slide, index) => (
                     <div key={index} className={`transition-all duration-1000 ease-in-out ${index === currentSlide ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'}`}>
                         {index === currentSlide && (
                             <>
                                <h1 className="text-5xl md:text-7xl font-extrabold font-boston-angel mb-4 text-shadow-lg leading-tight">
                                    {slide.heading}
                                </h1>
                                <p className="text-lg md:text-2xl font-lora text-shadow text-gray-200">
                                    {slide.subheading}
                                </p>
                             </>
                         )}
                     </div>
                   ))}
                </div>
            </div>
            <div className="absolute bottom-5 left-1/2 -translate-x-1/2 z-20 flex space-x-2">
                {heroSlides.map((_, index) => (
                    <button
                        key={index}
                        onClick={() => setCurrentSlide(index)}
                        className={`w-3 h-3 rounded-full transition-all duration-300 ${index === currentSlide ? 'bg-[#f6bb1d] scale-125' : 'bg-white bg-opacity-50'}`}
                    ></button>
                ))}
            </div>
        </section>
    );
};

