const BookingForm: React.FC = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        email: '',
        phone: '',
        cityCountry: '',
        eventType: '',
        eventDate: '',
        guestCount: '',
        venue: '',
        menuType: '',
        additionalServices: [] as string[],
        notes: ''
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };
    
    const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { value, checked } = e.target;
        setFormData(prev => {
            if (checked) {
                return { ...prev, additionalServices: [...prev.additionalServices, value] };
            } else {
                return { ...prev, additionalServices: prev.additionalServices.filter(service => service !== value) };
            }
        });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        // Here you would typically send the form data to a server
        console.log('Form Submitted', formData);
        alert('Thank you for your booking request! Our team will contact you shortly.');
    };

    return (
        <section id="booking" className="py-20 bg-[#002a08]">
            <div className="container mx-auto px-6">
                <div className="max-w-4xl mx-auto text-center">
                    <h2 className="text-4xl md:text-5xl font-bold text-white mb-4">Book Your Event with Golden Trees Caterers</h2>
                    <p className="text-gray-300 mb-8">Planning your event begins with one conversation. Fill out the booking form below and our team will contact you to discuss your date, theme, and preferences.</p>
                </div>
                <form onSubmit={handleSubmit} className="max-w-4xl mx-auto bg-[#0b5e04] p-8 rounded-lg shadow-2xl">
                    <div className="grid md:grid-cols-2 gap-6">
                        {/* Form Fields */}
                        <InputField label="Full Name" name="fullName" type="text" value={formData.fullName} onChange={handleChange} placeholder="Your full name" />
                        <InputField label="Email Address" name="email" type="email" value={formData.email} onChange={handleChange} placeholder="you@example.com" />
                        <InputField label="Phone / WhatsApp Number" name="phone" type="tel" value={formData.phone} onChange={handleChange} placeholder="+92 300 1234567" />
                        <InputField label="City / Country" name="cityCountry" type="text" value={formData.cityCountry} onChange={handleChange} placeholder="e.g., Karachi or USA" />
                        <SelectField label="Select Event Type" name="eventType" value={formData.eventType} onChange={handleChange} options={eventTypes} />
                        <InputField label="Event Date" name="eventDate" type="date" value={formData.eventDate} onChange={handleChange} />
                        <InputField label="Number of Guests" name="guestCount" type="number" value={formData.guestCount} onChange={handleChange} placeholder="Approx. 50" />
                        <InputField label="Venue Location (if confirmed)" name="venue" type="text" value={formData.venue} onChange={handleChange} placeholder="Optional" />
                        <SelectField label="Select Menu Type" name="menuType" value={formData.menuType} onChange={handleChange} options={menuTypes} />
                        
                        <div className="md:col-span-2">
                            <label className="block text-sm font-semibold text-[#f6bb1d] mb-2">Additional Services Needed</label>
                            <div className="grid grid-cols-2 sm:grid-cols-3 gap-4">
                                {additionalServices.map(service => (
                                    <label key={service} className="flex items-center space-x-2 text-white cursor-pointer">
                                        <input type="checkbox" name="additionalServices" value={service} onChange={handleCheckboxChange} className="form-checkbox h-5 w-5 text-[#10ff93] bg-gray-800 border-gray-600 rounded focus:ring-[#10ff93]"/>
                                        <span>{service}</span>
                                    </label>
                                ))}
                            </div>
                        </div>

                        <div className="md:col-span-2">
                            <label htmlFor="notes" className="block text-sm font-semibold text-[#f6bb1d] mb-2">Special Instructions or Notes</label>
                            <textarea id="notes" name="notes" value={formData.notes} onChange={handleChange} rows={4} className="w-full bg-[#002a08] border border-gray-600 text-white rounded-md p-2.5 focus:ring-[#f6bb1d] focus:border-[#f6bb1d] transition" placeholder="Any special requests, themes, or details..."></textarea>
                        </div>
                    </div>
                    <div className="mt-8 text-center">
                        <button type="submit" className="bg-[#f6bb1d] text-[#002a08] font-bold py-3 px-12 rounded-full text-lg hover:bg-[#ffde36] transition-all duration-300 transform hover:scale-105">
                            Reserve Your Date
                        </button>
                    </div>
                </form>
            </div>
        </section>
    );
};

interface InputFieldProps {
    label: string;
    name: string;
    type: string;
    value: string;
    onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
    placeholder?: string;
}

const InputField: React.FC<InputFieldProps> = ({ label, name, type, value, onChange, placeholder }) => (
    <div>
        <label htmlFor={name} className="block text-sm font-semibold text-[#f6bb1d] mb-2">{label}</label>
        <input type={type} id={name} name={name} value={value} onChange={onChange} placeholder={placeholder} required={type !== 'text' || name !== 'venue'} className="w-full bg-[#002a08] border border-gray-600 text-white rounded-md p-2.5 focus:ring-[#f6bb1d] focus:border-[#f6bb1d] transition" />
    </div>
);

interface SelectFieldProps {
    label: string;
    name: string;
    value: string;
    onChange: (e: React.ChangeEvent<HTMLSelectElement>) => void;
    options: string[];
}

const SelectField: React.FC<SelectFieldProps> = ({ label, name, value, onChange, options }) => (
    <div>
        <label htmlFor={name} className="block text-sm font-semibold text-[#f6bb1d] mb-2">{label}</label>
        <select id={name} name={name} value={value} onChange={onChange} required className="w-full bg-[#002a08] border border-gray-600 text-white rounded-md p-2.5 focus:ring-[#f6bb1d] focus:border-[#f6bb1d] transition">
            <option value="" disabled>-- Select an option --</option>
            {options.map(opt => <option key={opt} value={opt}>{opt}</option>)}
        </select>
    </div>
);

